const details = async (req, res) => {
  const {account, domain, predicates, workspace} = req

  const endpoint = `https://${account}.${domain}/${workspace.endpoint}/__graphql`

  res.json({
    endpoint,
    predicates,
    workspace,
  })
}

module.exports = details
